    List<string> monthNames = new List<string>();
    List<string> days = new List<string>();
    List<WeekClass> weeks = new List<WeekClass> ();
    DateTime startDate = new DateTime(DateTime.Now.Year, DateTime.Now.Month, 1);
    DateTime endDate = (new DateTime(DateTime.Now.Year, DateTime.Now.Month, 1)).AddMonths(1).AddYears(-1);
    bool displayModal = false;

    protected override void OnInitialized(){
        monthNames = System.Globalization.CultureInfo.CurrentCulture.DateTimeFormat.MonthGenitiveNames.ToList();
        GenerateCalendarHeader();
        GenerateCalendarBody();
    }
    private void LocalCalendar(ChangeEventArgs e){
        var month = e.Value.ToString();
        int monthIndex = DateTime.ParseExact(month, "MMMM", System.Globalization.CultureInfo.CreateSpecificCulture("en-GB")).Month; 
        startDate =  new DateTime(DateTime.Now.Year, monthIndex, 1);
        endDate = (new DateTime(DateTime.Now.Year, monthIndex, 1)).AddMonths(1).AddYears(-1);
    }

    private void GenerateCalendarHeader(){
        var day1= new List<string>();
        for(var dt = startDate; dt <= endDate; dt = dt.AddDays(1)){
            day1.Add(dt.ToString("dddd"));
        }
        days = day1.Distinct().ToList();
    }
    private void GenerateCalendarBody(){
        weeks = new List<WeekClass>();

        int flag = 0;
        WeekClass week = new WeekClass();
        List<Event> dates = new List<Event>();
        var totalDays = (int)(endDate - startDate).TotalDays;
        int countDays= 0;

        for(var dt = startDate; dt <= endDate; dt = dt.AddDays(1)){
            flag++;

            dates.Add(new Event(){
                value = dt.ToString("dd-MMMM-yyyy"),
                dayName = dt.ToString("dddd")
            });
            if(flag == 7){
                week = new WeekClass();
                week.Dates = dates;
                weeks.Add(week);

                dates = new List<Event>();
                    flag = 0;
            }
            if(countDays == totalDays){
                week = new WeekClass();
                week.Dates = dates;
                weeks.Add(week);
                break;
            }
            countDays++;
        }
    }
    private void CloseModal()
    {
        displayModal = false;
    }
    private void OpenModal(int wIndex, int dIndex)
    {
        displayModal = true;
    }   

